// src/App.js
import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Route, Routes, useNavigate } from 'react-router-dom'; // Ensure Router is imported correctly
import HomePage from './components/HomePage';
import NavBar from './components/NavBar';
import AboutPage from './components/AboutPage';
import MemberPage from './components/MemberPage';
import Login from './components/Login';
import SignUp from './components/SignUp';
import Logout from './components/Logout';
import BodyPage from './components/BodyPage';
import Profile from './components/Profile';
import EditProfile from './components/EditProfile'; 
import News from './components/News';

function App() {
    const [isDarkMode, setIsDarkMode] = useState(false);
    const [user, setUser] = useState({
        name: "John Doe",
        photoUrl: "path/to/photo.jpg",
        bio: "Software Developer",
        skills: ["JavaScript", "React", "Node.js"],
        appliedJobs: [],
        savedJobs: [],
        savedEvents: [],
        education: [
            { degree: "Bachelor's", field: "Computer Science", institution: "University A", startYear: 2015, endYear: 2019 }
        ],
        experience: [
            { position: "Software Engineer", company: "Company X", startYear: 2020, endYear: 2023, highlights: ["Led projects", "Mentored juniors"] }
        ]
    });

    const navigate = useNavigate(); // Initialize the navigate function

    // Load the user's preference from localStorage
    useEffect(() => {
        const savedMode = localStorage.getItem('darkMode') === 'true';
        setIsDarkMode(savedMode);
    }, []);

    const toggleDarkMode = () => {
        const newMode = !isDarkMode;
        setIsDarkMode(newMode);
        localStorage.setItem('darkMode', newMode); 

        const iframe = document.getElementById('news-iframe'); 
        if (iframe) {
            iframe.contentWindow.postMessage({ darkMode: newMode }, '*');
        }
    };

    const handleSaveProfile = (updatedUser) => {
        setUser(updatedUser); // Update user state with the edited profile
        navigate('/profile'); // Navigate back to the profile after saving
    };

    return (
        <div className={isDarkMode ? 'bg-dark text-white' : 'bg-light text-dark'}>
            <NavBar isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />
            <Routes>
                <Route exact path='/' element={<HomePage isDarkMode={isDarkMode} />} />
                <Route path="/signup" element={<SignUp />} />
                <Route path="/login" element={<Login />} />
                <Route path="/body" element={<BodyPage isDarkMode={isDarkMode} />} />
                <Route path="/about" element={<AboutPage />} />
                <Route path="/member/:name" element={<MemberPage />} />
                <Route path="/profile" element={<Profile user={user} onEdit={() => navigate('/edit-profile')} />} />
                <Route path="/edit-profile" element={<EditProfile user={user} onSave={handleSaveProfile} />} /> {/* Pass props to EditProfile */}
                <Route path="/logout" element={<Logout />} />
                <Route path="/news" element={<News isDarkMode={isDarkMode} />} />
            </Routes>
        </div>
    );
}

export default App;
