// src/App.js
import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './components/HomePage';
import NavBar from './components/NavBar';
import AboutPage from './components/AboutPage';
import MemberPage from './components/MemberPage';
import Login from './components/Login';
import SignUp from './components/SignUp';
import Logout from './components/Logout';
import BodyPage from './components/BodyPage'; 
import Profile from './components/Profile'; 
import News from './components/News';

function App() {
    const [isDarkMode, setIsDarkMode] = useState(false);
    const [showNewsModal, setShowNewsModal] = useState(false);
    const [newsCategory, setNewsCategory] = useState('general');

    // Load the user's preference from localStorage
    useEffect(() => {
        const savedMode = localStorage.getItem('darkMode') === 'true';
        setIsDarkMode(savedMode);
    }, []);

    const toggleDarkMode = () => {
        setIsDarkMode(prevMode => !prevMode);
        localStorage.setItem('darkMode', !isDarkMode); // Persist the user's preference
    };

    const handleShowNews = (category = 'general') => {
        setNewsCategory(category);
        setShowNewsModal(true);
    };

    const handleCloseNewsModal = () => {
        setShowNewsModal(false);
    };

    return (
        <Router>
            <div className={isDarkMode ? 'bg-dark text-white' : 'bg-light text-dark'}>
                <NavBar isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} onShowNews={handleShowNews} />
                <Routes>
                    <Route exact path='/' element={<HomePage isDarkMode={isDarkMode} />} />
                    <Route path="/signup" element={<SignUp />} />
                    <Route path="/login" element={<Login />} />
                    <Route path="/body" element={<BodyPage isDarkMode={isDarkMode} />} /> 
                    <Route path="/about" element={<AboutPage />} />
                    <Route path="/member/:name" element={<MemberPage />} />
                    <Route path="/profile" element={<Profile />} /> 
                    <Route path="/logout" element={<Logout />} />
                </Routes>

                {/* Modal for News */}
                {showNewsModal && (
                    <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1" aria-labelledby="newsModalLabel" aria-hidden="true">
                        <div className="modal-dialog modal-lg">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title" id="newsModalLabel">News - {newsCategory.charAt(0).toUpperCase() + newsCategory.slice(1)}</h5>
                                    <button type="button" className="btn-close" onClick={handleCloseNewsModal} aria-label="Close"></button>
                                </div>
                                <div className="modal-body">
                                    <News key={newsCategory} pageSize={15} country='us' category={newsCategory} />
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </Router>
    );
}

export default App;
