// src/App.js
import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './components/HomePage';
import NavBar from './components/NavBar';
import AboutPage from './components/AboutPage';
import MemberPage from './components/MemberPage';
import Login from './components/Login';
import SignUp from './components/SignUp';
import Logout from './components/Logout';
import BodyPage from './components/BodyPage'; // Import BodyPage
import Profile from './components/Profile'; // Import Profile component

function App() {
    const [isDarkMode, setIsDarkMode] = useState(false);

    const toggleDarkMode = () => {
        setIsDarkMode(prevMode => !prevMode);
        localStorage.setItem('darkMode', !isDarkMode); // Persist the user's preference
    };

    return (
        <Router>
            <div className={isDarkMode ? 'bg-dark text-white' : 'bg-light text-dark'}>
                <NavBar isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />
                <Routes>
                    <Route exact path='/' element={<HomePage isDarkMode={isDarkMode} />} />
                    <Route path="/signup" element={<SignUp />} />
                    <Route path="/login" element={<Login />} />
                    <Route path="/body" element={<BodyPage />} />
                    <Route path="/about" element={<AboutPage />} />
                    <Route path="/member/:name" element={<MemberPage />} />
                    <Route path="/profile" element={<Profile />} /> {/* Add the route for Profile */}
                    <Route path="/logout" element={<Logout />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
