import React, { useContext, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import JobContext from '../context/Jobs/jobsContext';
import JobsItem from './JobsItem';
import 'bootstrap/dist/css/bootstrap.min.css';

const Jobs = () => {
  const navigate = useNavigate();
  const context = useContext(JobContext);
  const { jobs, getalljobs } = context;

  useEffect(() => {
    getalljobs();
  }, [getalljobs]);

  if (!jobs || jobs.length === 0) {
    return <div>No jobs available</div>;
  }
  const ref = useRef(null)


  const UpdateJob = () => {
    ref.current.click()

  }



  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center">
        <h1>Available Jobs</h1>
        <button
          className="btn btn-primary"
          onClick={() => navigate('/AddJob')}
        >
          Add Job
        </button>
        {/* <!-- Modal Button --> */}
        <button type="button" ref={ref}  class="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">
          Launch demo modal
        </button>


        {/* <!-- Modal --> */}
        <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div class="modal-dialog"  role="document">
            <div class="modal-content">
              <div class="modal-header">
                <h5 class="modal-title" id="exampleModalLabel">Edit Jobs</h5>
                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div class="modal-body">
                ...
              </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                <button type="button" class="btn btn-primary">Save changes</button>
              </div>
            </div>
          </div>
        </div>


      </div>
      <div className="row mt-4">
        {jobs.map((job) => (
          <div key={job._id} className="col-md-4 mb-3">
            <JobsItem job={job} />
          </div>
        ))}
      </div>
    </div>
  );
}
export default Jobs;
