import React, { useState } from 'react';

const Jobs = () => {


    const [showForm, setShowForm] = useState(false);
    const [newJob, setNewJob] = useState({
        jobTitle: '',
        jobCompany: '',
        jobName: '',
        jobType: '',
        jobSalary: '',
        jobDescription: ''
    });

    // Function to handle form field changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewJob((prevJob) => ({
            ...prevJob,
            [name]: value
        }));
    };

    // Function to add a new job
    const addJob = (e) => {
        e.preventDefault();
        setJobs((prevJobs) => [...prevJobs, newJob]);
        setNewJob({
            jobTitle: '',
            jobCompany: '',
            jobName: '',
            jobType: '',
            jobSalary: '',
            jobDescription: ''
        });
        setShowForm(false); // Hide form after adding the job
    };

    return (
        <div className="container my-4">
            <h2>Job Postings</h2>
            {/* Display the list of jobs */}
            <div className="job-list">
                {jobs.map((job, index) => (
                    <div key={index} className="job-posting mb-3">
                        <h5>{job.jobTitle}</h5>
                        <p><strong>Company:</strong> {job.jobCompany}</p>
                        <p><strong>Job Name:</strong> {job.jobName}</p>
                        <p><strong>Job Type:</strong> {job.jobType}</p>
                        <p><strong>Salary:</strong> {job.jobSalary}</p>
                        <p><strong>Description:</strong> {job.jobDescription}</p>
                        <hr />
                    </div>
                ))}
            </div>

            {/* Button to show/hide the form */}
            <button className="btn btn-primary mb-4" onClick={() => setShowForm(!showForm)}>
                {showForm ? 'Cancel' : 'Add Job'}
            </button>

            {/* Form to add a new job */}
            {showForm && (
                <form onSubmit={addJob} className="job-form">
                    <div className="mb-3">
                        <label htmlFor="jobTitle" className="form-label">Job Title</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="jobTitle" 
                            name="jobTitle" 
                            value={newJob.jobTitle}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="jobCompany" className="form-label">Company</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="jobCompany" 
                            name="jobCompany" 
                            value={newJob.jobCompany}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="jobName" className="form-label">Job Name</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="jobName" 
                            name="jobName" 
                            value={newJob.jobName}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="jobType" className="form-label">Job Type</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="jobType" 
                            name="jobType" 
                            value={newJob.jobType}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="jobSalary" className="form-label">Job Salary</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="jobSalary" 
                            name="jobSalary" 
                            value={newJob.jobSalary}
                            onChange={handleInputChange}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="jobDescription" className="form-label">Job Description</label>
                        <textarea 
                            className="form-control" 
                            id="jobDescription" 
                            name="jobDescription" 
                            value={newJob.jobDescription}
                            onChange={handleInputChange}
                            rows="4"
                            required
                        ></textarea>
                    </div>
                    <button type="submit" className="btn btn-success">Add Job</button>
                </form>
            )}
        </div>
    );
};

export default Jobs;
