import React, { useContext } from 'react';
import JobContext from '../context/Jobs/jobsContext'; // Correct import path
import JobsItem from './JobsItem';

const Jobs = () => {
  const context = useContext(JobContext); // Ensure that the context is being used properly

  // Check if context and jobs exist before destructuring
  if (!context || !context.jobs) {
    return <div>No jobs available</div>; // Handle the case where jobs are not yet loaded
  }

  const { jobs } = context;

  return (
    <div>
      <h1>Available Jobs</h1>
      {jobs.map((job) => {
        // <div key={job._id}>
        //   <h3>{job.jobTitle}</h3>
        //   <p>{job.jobDescription}</p>
        //   <p>Company: {job.jobCompany}</p>
        //   <p>Salary: ${job.jobSalary}</p>
        // </div>
        return <JobsItem Jobs= {jobs} />
      })}
    </div>
  );
};

export default Jobs;
