import React, { useState, useContext } from 'react';
import JobContext from '../context/Jobs/jobsContext';

const AddJob = () => {
  const { addJob } = useContext(JobContext); // Assuming addJob is a function in JobContext for adding a job
  const [jobDetails, setJobDetails] = useState({
    jobTitle: '',
    jobDescription: '',
    jobCompany: '',
    jobSalary: '',
  });

  const handleChange = (e) => {
    setJobDetails({ ...jobDetails, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addJob(jobDetails); // Call context function to add a new job
    setJobDetails({ jobTitle: '', jobDescription: '', jobCompany: '', jobSalary: '' }); // Reset form
  };

  return (
    <div className="container my-4">
      <h2 className="text-center mb-4">Post a New Job</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="jobTitle" className="form-label">Job Title</label>
          <input
            type="text"
            className="form-control"
            id="jobTitle"
            name="jobTitle"
            value={jobDetails.jobTitle}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="jobDescription" className="form-label">Job Description</label>
          <textarea
            className="form-control"
            id="jobDescription"
            name="jobDescription"
            rows="3"
            value={jobDetails.jobDescription}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div className="mb-3">
          <label htmlFor="jobCompany" className="form-label">Company</label>
          <input
            type="text"
            className="form-control"
            id="jobCompany"
            name="jobCompany"
            value={jobDetails.jobCompany}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="jobSalary" className="form-label">Salary</label>
          <input
            type="number"
            className="form-control"
            id="jobSalary"
            name="jobSalary"
            value={jobDetails.jobSalary}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary w-100">Add Job</button>
      </form>
    </div>
  );
};

export default AddJob;
