import React, { useContext, useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import JobContext from '../context/Jobs/jobsContext';
import JobsItem from './JobsItem';
import 'bootstrap/dist/css/bootstrap.min.css';

const Jobs = () => {
  const navigate = useNavigate();
  const context = useContext(JobContext);
  const { jobs, getalljobs, editjob } = context;

  const [currentJob, setCurrentJob] = useState(null);
  const ref = useRef(null);  // For triggering the modal open

  useEffect(() => {
    getalljobs();
  }, [getalljobs]);

  if (!jobs || jobs.length === 0) {
    return <div>No jobs available</div>;
  }

  const handleUpdateJob = (job) => {
    setCurrentJob(job);  // Set job data for modal
    ref.current.click();  // Open modal
  };

  const handleEditSubmit = () => {
    if (currentJob) {
      editjob(currentJob._id, currentJob.jobTitle, currentJob.jobDescription, currentJob.jobSalary, currentJob.jobType, currentJob.jobCompany);
    }
  };

  const handleChange = (e) => {
    setCurrentJob({ ...currentJob, [e.target.name]: e.target.value });
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center">
        <h1>Available Jobs</h1>
        <button className="btn btn-primary" onClick={() => navigate('/AddJob')}>Add Job</button>
        <button type="button" ref={ref} className="btn btn-primary d-none" data-toggle="modal" data-target="#exampleModal">
          Launch Modal
        </button>
      </div>

      {/* Modal */}
      <div className="modal fade" id="exampleModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">Edit Job</h5>
              <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              {/* Form Fields for Job Edit */}
              <input type="text" name="jobTitle" value={currentJob?.jobTitle || ''} onChange={handleChange} className="form-control mb-2" placeholder="Job Title" />
              <textarea name="jobDescription" value={currentJob?.jobDescription || ''} onChange={handleChange} className="form-control mb-2" placeholder="Job Description"></textarea>
              <input type="text" name="jobCompany" value={currentJob?.jobCompany || ''} onChange={handleChange} className="form-control mb-2" placeholder="Company Name" />
              <input type="number" name="jobSalary" value={currentJob?.jobSalary || ''} onChange={handleChange} className="form-control mb-2" placeholder="Salary" />
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary" onClick={handleEditSubmit} data-dismiss="modal">Save changes</button>
            </div>
          </div>
        </div>
      </div>

      <div className="row mt-4">
        {jobs.map((job) => (
          <div key={job._id} className="col-md-4 mb-3">
            <JobsItem job={job} UpdateJob={handleUpdateJob} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Jobs;
